mapper.select:
select p.patterid,p.userid,p.roleid,p.courseid,p.patterTypeId,p.contentTypeId,p.shortTitle, p.content,p.create_time,p.update_time,p.update_id,p.isshare,p.note,p.dr, u.username username from hj_patter p left join hj_user u on u.userid = p.userid where 1=1 and p.dr = 0
and p.patterTypeId = #{patterTypeId}
and (p.isshare = 1 or (p.isshare = 0 and p.userid = #{userid}))
